pragma solidity ^0.8.22;

// legacy db contract interface
interface IDB {
    struct pf {
        uint id;
        uint class;
        bool isAien;
        uint usedAienId;
    }

    struct pfGrade {
        uint normal;
        uint uncommon;
        uint rare;
        uint unique;
        uint legendary;
        uint myth;
        uint ancient;
    }

    struct aien {
        uint id;
        // 강화 횟수
        uint mixCount;
        //
        uint p2Level;
        // 토탈 경험치
        uint totalExp;
        // 미션 및, 인플루언서 활동 관련 경험치
        uint influExp;
        // 기본 확률
        uint baseProb;
        // 토탈 확률
        uint totalProb;
        // is PF
        uint isPFid;
        // 추가확률
        uint addProb;
    }

    struct User {
        // DB > idx
        uint userId;
        // token itme ID
        uint itemId;
        address incomeAddr;
        uint feeBalance;
        bool isAble;
        uint mintCount;
        uint useLevelupCount;
        uint useMergeCount;
        // uint ownerIncomePercent;
        // uint userIncomPercent;
        //레퍼럴로 얻은 수익
        uint referralIncome;
        address agency;
        uint agencyIncome;
    }

    function AIENS(uint _key) external view returns (aien memory);

    function PFS(uint _key) external view returns (pf memory);

    function getAien(uint _id) external view;

    function getPF(uint _id) external view;

    function getPfGrade(uint _id) external view returns (uint);

    function setAien(uint _id) external;

    function usePFimg(uint _aienId, uint _pfId) external;

    function setAienAll(
        uint _id,
        uint _mixCount,
        uint _p2Level,
        uint _totalExp,
        uint _influExp,
        uint _baseProb,
        uint _totalProb,
        uint _isPFid,
        uint _addProb
    ) external;

    function getContractAddr(
        string memory _name
    ) external view returns (address);

    function burnValue(uint _burnAmount) external;

    function setAienGradeInfo(uint _toGrade) external;

    function subPfGrades(uint _pfId) external;

    function setLevelUpStatus(uint _toGrade) external;

    function _failedAienSet(
        uint _id,
        uint _totalExp,
        uint _influExp,
        uint _baseProb,
        uint _addProb
    ) external;

    function _successAienSet(
        uint _id,
        uint _p2Level,
        uint _totalExp,
        uint _influExp,
        uint _baseProb,
        uint _addProb
    ) external;

    function _mergeCalcul(
        uint _itemId
    ) external view returns (uint, address, uint, address, uint, uint);

    function _mergeCalcul2(
        uint _itemId
    ) external view returns (uint, address, uint, address, uint, uint);

    function _influencerMerge(
        address _to,
        uint _itemId,
        uint _referralIncome
    ) external returns (uint, string memory);

    function _levelUpCalcul(
        uint _itemId,
        uint _fee
    ) external returns (address, address, uint, uint);

    function _levelUpSucess(uint _id, uint _p2Level) external;

    function _levelUpFailed(uint _id, uint _addProb) external;

    function _mergeCount(
        address _agency,
        uint _agencyFee,
        address _influencer,
        uint _influencerFee
    ) external;

    function usePFPower(uint _id, uint _usePower) external;

    function addProbFee() external view returns (uint);

    function PfGrades() external view returns (pfGrade memory);

    function getAienGradeInfo() external view returns (uint[] memory);

    function basicMergeAmount() external view returns (uint);

    function influencerMergeAmount() external view returns (uint);

    function _getMedataMargin() external view returns (uint, uint, uint);

    function getAienLevel(uint _aienId) external view returns (uint);

    function setPerprojectWallet(address _addr) external;

    function setAgencyAddr(address _influencer, address _agency) external;

    function setAgencyIncomePercent(uint _agencyIncomePercent) external;

    function adminSetMergeAmount(
        uint _influencerMergeAmount,
        uint _basicMergeAmount,
        uint _agencyIncomePercent,
        uint _ownerIncomePercent
    ) external;

    function getUserFromItem(uint _itemId) external view returns (User memory);

    function adminSetMetaData(
        uint _pfId,
        uint8 _grade,
        string memory _seedHash
    ) external;

    function _itemMergeFromDiamond(
        address _to,
        uint _pfId,
        string memory _seedHash,
        uint8 _grade
    ) external;
}
","keccak256":"0xbf4ffb9733d65fe3597c56dd6ff80ec21176dd3f0e43104f9ddf97e897d281cd","license":"MIT"},"contracts/Channel_iN/interfaces/IP1.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

interface IP1 {
    struct PendingInfo {
        address user;
        // 1: 출금요청,
        // 2: 출금취소,
        // 3: 완료,
        // 4: 취소완료: 완료시 재예치 버튼 활성화
        uint8 pendingType;
        uint startTime;
        uint endTime;
        uint amount;
        // pending Id
        uint pendingId;
    }

    function update() external returns (uint);

    function diamond_P1_deposit(
        address _sender,
        uint _amount
    ) external returns (uint);

    function diamond_P1_reDposit(address _sender) external returns (uint);

    function diamond_P1_harvest(address _sender) external returns (uint);

    function diamond_P1_pendingReward(
        address _sender,
        uint _withdrawBlock
    ) external view returns (uint);

    function diamond_P1_addPower(
        address _sender,
        uint _aienId,
        uint _usePower
    ) external returns (uint);

    function diamond_P1_widthdraw(
        address _sender,
        uint _amount
    ) external returns (uint,uint);

    function diamond_P1_widthdrawConfirm(
        address _sender,
        uint _pendingId
    ) external returns (uint,uint);

    function diamond_P1_widthdrawCancel(
        address _sender,
        uint _pendingId
    ) external returns (uint, uint);

    function diamond_P1_widthdrawCancelConfirm(
        address _sender,
        uint _pendingId
    ) external returns (uint, uint);

    function diamond_P1_getPoolData() external view returns (uint, uint, uint);

    function diamond_P1_getUserData(
        address _sender
    ) external view returns (uint, uint, uint, uint, uint);

    function diamond_P1_getUnstakeData(
        address _sender
    ) external view returns (PendingInfo[] memory);

    function diamond_P1_addDistributionAmountAll(uint _amount) external;

    function unStakeTimeLock() external view returns (uint16);

    function unStakeCancelTimeLock() external view returns (uint16);
}
