","keccak256":"0x86c1470cbfd878491e5de030072b647352d36bd27122cffb928970b1945282aa","license":"MIT"},"contracts/Channel_iN/interfaces/IP1.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

interface IP1 {
    struct PendingInfo {
        address user;
        // 1: 출금요청,
        // 2: 출금취소,
        // 3: 완료,
        // 4: 취소완료: 완료시 재예치 버튼 활성화
        uint8 pendingType;
        uint startTime;
        uint endTime;
        uint amount;
        // pending Id
        uint pendingId;
    }

    function update() external returns (uint);

    function diamond_P1_deposit(
        address _sender,
        uint _amount
    ) external returns (uint);

    function diamond_P1_reDposit(address _sender) external returns (uint);

    function diamond_P1_harvest(address _sender) external returns (uint);

    function diamond_P1_pendingReward(
        address _sender,
        uint _withdrawBlock
    ) external view returns (uint);

    function diamond_P1_addPower(
        address _sender,
        uint _aienId,
        uint _usePower
    ) external returns (uint);

    function diamond_P1_widthdraw(
        address _sender,
        uint _amount
    ) external returns (uint,uint);

    function diamond_P1_widthdrawConfirm(
        address _sender,
        uint _pendingId
    ) external returns (uint,uint);

    function diamond_P1_widthdrawCancel(
        address _sender,
        uint _pendingId
    ) external returns (uint, uint);

    function diamond_P1_widthdrawCancelConfirm(
        address _sender,
        uint _pendingId
    ) external returns (uint, uint);

    function diamond_P1_getPoolData() external view returns (uint, uint, uint);

    function diamond_P1_getUserData(
        address _sender
    ) external view returns (uint, uint, uint, uint, uint);

    function diamond_P1_getUnstakeData(
        address _sender
    ) external view returns (PendingInfo[] memory);

    function diamond_P1_addDistributionAmountAll(uint _amount) external;

    function unStakeTimeLock() external view returns (uint16);

    function unStakeCancelTimeLock() external view returns (uint16);
}
","keccak256":"0x07efaa8c8bc5cf474a039cab55457d26d607d1eb1a5b133c27962cba3bba2b39","license":"MIT"},"contracts/Channel_iN/interfaces/IP2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

interface IP2 {
    struct User {
        bool isBlockUser;
        uint perRewarded;
        uint usdtRewarded;
        EnumerableSet.UintSet tokenIds;
    }

    struct Aien {
        address staker;
        uint level;
        // requires value
        uint rewardPer;
        uint rewardUsdt;
        uint rewardUsdtDebt;
        uint rewardPerDebt;
        ////////////////////
        uint per_received;
        uint usdt_received;
    }

    struct Balances {
        // 로직상 계산에 필요한 밸런스 변수 (실제와 다를 수 있음)
        uint perBalance;
        uint usdtBalance;
        // 레이어가 오픈 되지 않은 상태에서
        // 레이어가 오픈되면 해당 레이어에 저장된 리워드를 데일리 리워드로 추가 분배하기 위한 변수
        uint savedPerBalance;
        uint savedUsdtBalance;
        // 현재 savedUsdt, savedPer를 통해 나온 데일리 리워드
        uint add_dailyUSDT;
        uint add_dailyPER;
        // 보안상 문제가 생겨
        // 예상보다 많은 withdraw를 요청하게 되는 경우
        // 지금까지 쌓인 레이어별 토탈 밸런스와
        // 지금까지 쌓인 레이어별 출금 밸런스를 비교하여
        // 출금 가능한지 체크하는 변수
        uint total_checkWithdrawPER;
        uint withdrawal_checkWithdrawPER;
        uint total_checkWithdrawUSDT;
        uint withdrawal_checkWithdrawUSDT;
    }

    struct Layer {
        Balances balances;
        // P2에서 해당 레이어에 토큰 배정 받을때 리워드 퍼센트
        uint rewardUsdtPercent;
        uint rewardPerPercent;
        // 유저에게 하루에 분배하는 리워드 퍼센트
        uint dailyReward_Percent;
        // 계산에 필요
        uint rewardPer;
        uint rewardUsdt;
        // 미오픈시 저장한 리워드를 데일리 리워드로 추가 분배하기 위한 퍼센트변수
        uint add_dailyReward_Percent;
        uint lastRewardBlock;
        uint dailyRewardUpdateBlock;
        uint totalStakedAien;
        bool isOpen;
    }

    struct AienLoadData {
        //aien정보
        uint _aienId;
        uint _aienLevel;
        // 출금 토탈
        uint _aien_per_received;
        uint _aien_usdt_received;
        //출금 가능
        uint usdt_withdrawable;
        uint per_withdrawable;
        // block당 리워드
        uint block_reward_per;
        uint block_reward_usdt;
    }

    struct LayerLoadData {
        bool isOpen;
        uint _layerNumber;
        uint _24h_reward_per;
        uint _24h_reward_usdt;
        uint totalStakedAien;
    }
    struct UserLoadData {
        uint _usdtRewarded;
        uint _perRewarded;
        bool _isBlockUser;
    }

    function aiens(uint _id) external view returns (Aien memory);

    function diamond_P2_deposit(address _sender, uint _aienId) external;

    function diamond_P2_withdraw(address _sender, uint _aienId) external;

    function diamond_P2_harvest(address _sender, uint _aienId) external;

    function diamond_P2_getUserInfo(
        address _sender
    )
        external
        view
        returns (
            UserLoadData memory,
            AienLoadData[] memory,
            LayerLoadData[] memory
        );

    function diamond_P2_getLayerData(
        uint _number
    ) external view returns (uint, uint, uint);

    function addPerUsdtDistribution(uint _usdt, uint _per) external;

    function P2_usdtBalance() external view returns (uint);

    function P2_perBalance() external view returns (uint);

    function MAX_STAKING_LIMIT() external view returns (uint);

    function layers(uint _number) external view returns (Layer memory);

    function pendingReward(
        uint _aienId,
        uint _layerNumber,
        uint _withdrawBlock
    ) external view returns (uint, uint);
}
","keccak256":"0x2e90a8470442fec4da79c46739fcf8b958fe1503f519a5f7b2fa048697e2396d","license":"MIT"},"contracts/shared/facets/DistributeFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {AppStorage, LibAppStorage, Modifiers} from "../../shared/libraries/LibAppStorage.sol";
import {LibMeta} from "../../shared/libraries/LibMeta.sol";
import {IERC20} from "../../shared/interfaces/IERC20.sol";
import {LibDistribute} from "../libraries/LibDistribute.sol";

contract DistributeFacet is Modifiers {
    // 1차로 Distribute facet에 balance를 모으고
    // 패싯이 변경되었을때 transfer하여 balance migrate하기 위한 함수
    function Distribute_Transfer_Balance(
        address _facet,
        address _tokenAddr,
        uint _amount
    ) external onlyDev {
        AppStorage storage s = LibAppStorage.diamondStorage();
        address msgsender = LibMeta.msgSender();
        IERC20(_tokenAddr).transfer(msgsender, _amount);
    }

    function Distribute_p0LvUpDistribute(
        address _agency,
        uint _agencyAmount,
        address _influencer,
        uint _influencerAmount,
        uint _totalAmount
    ) external {
        LibDistribute.p0LvUpDistribute(
            _agency,
            _agencyAmount,
            _influencer,
            _influencerAmount,
            _totalAmount
        );
    }

    function Distribute_swapToDistribute() external {
        LibDistribute.swapToDistribute();
    }
}
","keccak256":"0xa7b9948db42261e255d0f39ff292393eb9f72cf7e3a4b31dc1c808511c59c5dd","license":"MIT"},"contracts/shared/interfaces/IDiamondCut.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

interface IDiamondCut {
    enum FacetCutAction {Add, Replace, Remove}
    // Add=0, Replace=1, Remove=2

    struct FacetCut {
        address facetAddress;
        FacetCutAction action;
        bytes4[] functionSelectors;
    }

    /// @notice Add/replace/remove any number of functions and optionally execute
    ///         a function with delegatecall
    /// @param _diamondCut Contains the facet addresses and function selectors
    /// @param _init The address of the contract or facet to execute _calldata
    /// @param _calldata A function call, including function selector and arguments
    ///                  _calldata is executed with delegatecall on _init
    function diamondCut(
        FacetCut[] calldata _diamondCut,
        address _init,
        bytes calldata _calldata
    ) external;

    event DiamondCut(FacetCut[] _diamondCut, address _init, bytes _calldata);
}
